sys_script_include.script:
var GetEmailAddress = Class.create();
// Extiende la clase global.AbstractAjaxProcessor
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
  // define la funcion getEmail.  
  // Crea una busqueda para el Usuario con GlideRecord.
  // Usa el parametro sysparm_userID pasado desde el cliente para hacer una búsqueda en la tabla User.
  // Return la email addressdesde el registro.
  getEmail: function() {
    var userRecord = new GlideRecord("sys_user");
    userRecord.get(this.getParameter('sysparm_userID'));
    return userRecord.email + '';
  },
  type: 'GetEmailAddress'
});
